<template>
  <div
    v-if="vipData.rewardBenefits.length"
    id="member-vip-benefits-cards"
    class="flex phone:flex-col flex-nowrap gap-[.625rem] w-full overflow-x-auto"
  >
    <div
      v-for="rewardBenefit in vipData.rewardBenefits"
      :key="rewardBenefit.currency_id"
      :id="`member-vip-benefits-card-content-${rewardBenefit.currency_id}`"
      class="flex flex-col gap-[.375rem]"
    >
      <div class="font-[NotoSansTC] font-bold text-[.875rem] leading-[1.0625rem] text-[var(--text-03)]">
        {{ getCurrencyCodeById(rewardBenefit.currency_id) }}
      </div>
      <div class="rounded-[.625rem] p-5 flex flex-col gap-[.625rem] bg-[var(--card-bg-02)]">
        <div
          v-for="item in benefitsArray"
          :key="item.column"
          class="flex flex-nowrap items-center gap-5 min-w-[15.125rem]"
        >
          <div v-html="item.imgFile"></div>
          <div class="h-[1.875rem] w-[.0625rem] bg-[var(--bg-line-02)]"></div>
          <div class="flex flex-col gap-1">
            <div
              class="font-[Helvetica] font-bold text-[.875rem] leading-[1rem] text-[var(--card-text-03)] whitespace-nowrap"
