              {{ $t(item.titleI18n) }}
            </div>
            <div
              class="font-[Helvetica] font-bold text-[1.25rem] leading-[1.4375rem] text-[var(--card-text-02)] whitespace-nowrap"
            >
              {{ Number(rewardBenefit[item.column]) >= 0 ? moneyFormat(rewardBenefit[item.column]) : 0 }}
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
